uot;公正&quot;, &quot;法治&quot;, &quot;爱国&quot;, &quot;敬业&quot;, &quot;诚信&quot;, &quot;友善&quot;);\n        var $i = $(&quot;&quot;).text(a[a_idx]);\n        a_idx = (a_idx + 1) % a.length;\n        var x = e.pageX,\n            y = e.pageY;\n        $i.css({\n            &quot;z-index&quot;: 9999999999999999999999999999999999999999999999999999999999999999','<blockquote>\r\n<p><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:15px\"><strong>JavaScript代码：</strong></span></span></p>\r\n</blockquote>\r\n\r\n<pre>\r\n<span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:15px\"><code>\r\n/* 鼠标特效 */\r\nvar a_idx = 0;\r\njQuery(document).ready(function ($) {\r\n    $(&quot;body&quot;).click(function (e) {\r\n        var a = new Array(&quot;富强&quot;, &quot;民主&quot;, &quot;文明&quot;, &quot;和谐&quot;, &quot;自由&quot;, &quot;平等&quot;, &quot;公正&quot;, &quot;法治&quot;, &quot;爱国&quot;, &quot;敬业&quot;, &quot;诚信&quot;, &quot;友善&quot;);\r\n        var $i = $(&quot;&quot;).text(a[a_idx]);\r\n        a_idx = (a_idx + 1) % a.length;\r\n        var x = e.pageX,\r\n            y = e.pageY;\r\n        $i.css({\r\n            &quot;z-index&quot;: 999999999999999999999999999999999999999999999999999999999999999999999,\r\n            &quot;top&quot;: y - 20,\r\n            &quot;left&quot;: x,\r\n            &quot;position&quot;: &quot;absolute&quot;,\r\n            &quot;font-weight&quot;: &quot;bold&quot;,\r\n            &quot;color&quot;: &quot;#ff6651&quot;\r\n        });\r\n        $(&quot;body&quot;).append($i);\r\n        $i.animate({\r\n                &quot;top&quot;: y - 180,\r\n                &quot;opacity&quot;: 0\r\n            },\r\n            1500,\r\n            function () {\r\n                $i.remove();\r\n            });\r\n    });\r\n});\r\n</code>\r\n</span></span></pre>\r\n\r\n<blockquote>\r\n<p><span style=\"font-family:Verdana,Geneva,sans-serif\"><span style=\"font-size:15px\"><strong>效果：</strong></span></span></p>\r\n</b